import React, { Component } from "react";
import Navbarr from "../Navbar/Navbar";
import StripeCheckout from "react-stripe-checkout";
export default class Bookticket extends Component {
  state = {
    input: {
      noofticket: "",
      ticketPrice: 0,
    },
    event: {},
  };
  bookTicket = (token) => {
    const data = {
      token: token,
      event: this.state.event,
      amount: 200,
    };
    Serverservice.bookticket(data)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  onChange = (event) => {
    const input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input: input,
    });
  };
  render() {
    return (
      <div>
        <Navbarr />

        <StripeCheckout
          stripeKey="pk_test_51Hp8UIADAJ7Tatd8OqTodI3sQLWaBaErZlWm3K76Jy2zZadBpR8HZZOVIg8mN1ZV2i0k42N2i3TjR4iGDXpil3JV00LldPAdBy"
          name="Buy Tickets"
          token={this.bookTicket}
        >
          <button type="submit" className="bookbutton mt-5">
            <a
              style={{ color: "white", textDecoration: "none" }}
              // href={"/booktickets/" + this.props.match.params.id}
            >
              Book Tickets
            </a>
          </button>
        </StripeCheckout>
      </div>
    );
  }
}
