import React, { Component } from "react";
import { Container, Row, Col, Button, Form } from "react-bootstrap";
import Navbarr from "../Navbar/Navbar";
import StripeCheckout from "react-stripe-checkout";
import { NavLink } from "react-router-dom";
// import p1 from "../../assets/p1.jpg";
import "../EventDetails/Eventdetail.css";

import m2 from "../../assets/m2.jpg";

import Serverservice from "../../Services/Serverservice";
// import { Container } from "react-bootstrap";
export default class EventDetails extends Component {
  state = {
    input: {
      noofticket: "",
      ticketPrice: 0,
    },
    event: {},
  };
  bookTicket = (token) => {
    const data = {
      token: token,
      event: this.state.event,
      amount: 200,
    };
    Serverservice.bookticket(data)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  onChange = (event) => {
    const input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input: input,
    });
  };

  componentDidMount() {
    const id = this.props.match.params.id;

    Serverservice.eventdetail(id)
      .then((response) => {
        this.setState({
          event: response.data.event,
          ticketPrice: response.data.event.ticketPrice,
        });
        console.log(this.state.event);
        console.log(response);
      })
      .catch((err) => {
        console.log(err.response);
      });
  }
  render() {
    return (
      <div className="banner">
        <Navbarr />
        <h1 className="text-center">Book Tickets</h1>
        <Container className="mt-5 " fluid>
          <Row>
            <Col>
              <Container>
                <img
                  className="ml-5"
                  src={m2}
                  style={{ width: "60%", borderRadius: "8px", height: "500px" }}
                  alt={"ALternate"}
                />
              </Container>
            </Col>
            <Col lg={8}>
              <Container className="mt-5">
                <h1>{this.state.event.eventname}</h1>
                <h4 className="text-muted">{this.state.event.eventtype}</h4>
                <p className="mt-4">{this.state.event.eventdescription}</p>

                <hr></hr>
                <h4>Event Details</h4>
                <Row className="mt-4">
                  <Col>
                    <h5>City</h5>
                    <h6>{this.state.event.eventcity}</h6>
                  </Col>
                  <Col>
                    <h5>Region</h5>
                    <h6>{this.state.event.eventregion}</h6>
                  </Col>
                  <Col>
                    <h5>Date</h5>
                    <h6>{this.state.event.eventdate}</h6>
                  </Col>
                  <Col>
                    <h5>Expected Seats</h5>
                    <h6>{this.state.event.audience}</h6>
                  </Col>
                  <Col>
                    <h5>Email</h5>
                    <h6>{this.state.event.email}</h6>
                  </Col>
                  <Col>
                    <h5>Ticket Price</h5>
                    <h6>{this.state.event.ticketPrice}</h6>
                  </Col>
                </Row>
                <Form>
                  <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Number of Seats</Form.Label>
                    <Form.Control
                      onChange={this.onChange}
                      name="noofticket"
                      as="select"
                    >
                      <option>1</option>
                      <option>2</option>
                      <option>3</option>
                      <option>4</option>
                      <option>5</option>
                    </Form.Control>
                  </Form.Group>
                </Form>

                <button className="bookbutton mt-5">
                  {/* <a
                    href={"/booktickets/" + this.props.match.params.id}
                    style={{ color: "white", textDecoration: "none" }}
                    // href={"/booktickets/" + this.props.match.params.id}
                  >
                    Book Tickets
                  </a> */}
                  <NavLink
                    style={{ color: "white", textDecoration: "none" }}
                    to={"/booktickets/" + this.props.match.params.id}
                  >
                    Book Tickets
                  </NavLink>
                </button>
              </Container>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
