import React, { Component } from "react";
import { Col, Form, Button, Row, Container } from "react-bootstrap";
import Navbarr from "../Navbar/Navbar";
import Serverservice from "../../Services/Serverservice";
import logo from "../../logo.svg";

export default class Verifyotp extends Component {
  state = {
    input: { otp: "" },
  };
  handleChange = (event) => {
    let input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input,
    });
  };
  handleClick = () => {
    const data = {
      otp: this.state.input.otp,
      email: localStorage.getItem("email"),
    };
    Serverservice.verifyOtp(data)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err.response);
      });
  };
  render() {
    return (
      <div>
        <Navbarr />
        <Container fluid>
          <Row className="justify-content-md-center mt-5">
            <Col></Col>
            <Col xs={4}>
              <h1 className="text-center">Signup</h1>
              <p className="mt-3">
                Good to have you in aou community sign up and enjoy all the
                advantages. Good to have you in aou community sign up and enjoy
                all the advantages. Good to have you in aou community sign up
                and enjoy all the advantages.
              </p>
            </Col>
            <Col xs={1}></Col>
            <Col xs={4}>
              <img src={logo} style={{ width: "150px", marginLeft: "30%" }} />
              <Form className=" ">
                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Enter Otp</Form.Label>
                  <Form.Control
                    type="text"
                    name="otp"
                    required
                    onChange={this.handleChange}
                    placeholder="Eg:- 125533"
                  />
                </Form.Group>

                <Button variant="dark" type="submit" onClick={this.handleClick}>
                  Submit
                </Button>
                <span className="ml-3">
                  Already a user?
                  <a href="/">Login</a>
                </span>
              </Form>
            </Col>
            <Col></Col>
          </Row>
        </Container>
      </div>
    );
  }
}
