import axios from "axios";
const BASE_URL = "http://localhost:8080/";
class Serverservice {
  signup = (data) => {
    // console.log("servic");
    // console.log(data);

    return axios.post(BASE_URL + "auth/signup", data);
  };
  login = (data) => {
    // console.log("servic");
    // console.log(data);

    return axios.post(BASE_URL + "auth/login", data);
  };
}
export default new Serverservice();
