import React, { Component } from "react";
import { Button, Container, Table } from "react-bootstrap";
import Navbarr from "../Navbar/Navbar";
import m2 from "../../assets/m2.jpg";
import m4 from "../../assets/m4.jpg";
import m3 from "../../assets/m3.jpg";
import m1 from "../../assets/m1.jpg";
import Serverservice from "../../Services/Serverservice";
import "../Yourevents/Yourevents.css";
export default class Yourevents extends Component {
  state = {
    events: [],
  };
  deleteevent = () => {
    console.log(this);
  };
  componentDidMount() {
    const data = {
      id: localStorage.getItem("id"),
    };
    Serverservice.getyourevents(data)
      .then((response) => {
        console.log(response);
        this.setState({
          events: response.data.events,
        });
        console.log(this.state.events);
      })
      .catch((err) => {
        console.log(err.response);
      });
  }
  render() {
    return (
      <div>
        <Navbarr />
        <Container>
          <h2 className="text-center mt-5">Your Events</h2>
          <div className="yourevents">
            {this.state.events.map((data) => {
              return (
                <div className="event mb-3">
                  <div style={{ margin: "20px 50px" }}>
                    <h3>{data.eventname}</h3>
                    <p className="text-muted">{data.eventtype}</p>
                    <p>{data.eventdescription}</p>
                    <Table striped bordered hover>
                      <thead>
                        <tr>
                          <th>Event Type</th>
                          <th>Event Region</th>
                          <th>Event City</th>
                          <th>Event Date</th>
                          <th>Expected Auidence </th>
                        </tr>
                      </thead>
                      <tbody>
                        <tr>
                          <td>{data.eventtype}</td>
                          <td>{data.eventregion}</td>
                          <td>{data.eventcity}</td>
                          <td>{data.eventdate}</td>
                          <td>{data.audience}</td>
                        </tr>
                      </tbody>
                    </Table>
                    <Button variant="danger" onClick={this.deleteevent}>
                      Delete
                    </Button>
                  </div>
                </div>
              );
            })}
          </div>
        </Container>
      </div>
    );
  }
}
