import React, { Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import Serverservice from "../../Services/Serverservice";
import "../Eventbycategory/Eventbycategory.css";
import { Link } from "react-router-dom";
import Navbarr from "../Navbar/Navbar";
import EventCard from "../EventCard/EventCard";
export default class Eventbycategory extends Component {
  state = {
    event: [],
  };
  componentWillUnmount() {
    console.log("unmounted");
  }
  componentDidUpdate() {
    console.log("mounted");
    const eventtype = this.props.match.params.eventtype;
    console.log(eventtype);

    Serverservice.eventbycategory(eventtype)
      .then((response) => {
        console.log(response);
        this.setState({
          event: response.data.events,
        });
      })
      .catch((err) => {
        console.log(err.response);
      });
  }
  render() {
    return (
      <React.Fragment>
        <Navbarr />
        <Container className="mt-5" fluid>
          <Row>
            <Col>
              <Container className="sidebar">
                <div style={{ margin: "10px 20px" }}>
                  <h5>Search Events</h5>
                  <hr></hr>
                  <h6>Event Type</h6>
                  <ul className="ml-5">
                    <li>
                      <Link to="/">Sports</Link>
                    </li>
                    <li>
                      <Link to="/">Plays</Link>
                    </li>
                    <li>
                      <Link to="/">Workshops</Link>
                    </li>
                    <li>
                      <Link to="/">Comedy</Link>
                    </li>
                  </ul>
                  <hr />
                  <h6>Event City</h6>
                  <ul className="ml-5">
                    <li>
                      <Link to="/">Delhi</Link>
                    </li>
                    <li>
                      <Link to="/">Mumbai</Link>
                    </li>
                    <li>
                      <Link to="/">Kolkata</Link>
                    </li>
                    <li>
                      <Link to="/">Chennai</Link>
                    </li>
                  </ul>
                  <hr />
                </div>
              </Container>
            </Col>
            <Col xs={9}>
              {this.state.event.map((event) => {
                return (
                  <Container>
                    <EventCard />
                  </Container>
                );
              })}
            </Col>
          </Row>
        </Container>
      </React.Fragment>
    );
  }
}
