import React, { Component } from "react";
import { Container, Row, Col, Button, Form } from "react-bootstrap";
import Navbarr from "../Navbar/Navbar";
// import p1 from "../../assets/p1.jpg";
import "../EventDetails/Eventdetail.css";

import m2 from "../../assets/m2.jpg";

import Serverservice from "../../Services/Serverservice";
// import { Container } from "react-bootstrap";
export default class EventDetails extends Component {
  state = {
    input: {
      noofticket: "",
    },
    event: {},
  };
  onChange = (event) => {
    const input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input: input,
    });
  };
  booktickets = (event, ticketPrice) => {
    event.preventDefault();
    console.log(this.state.input.noofticket * ticketPrice);
  };

  componentDidMount() {
    const id = this.props.match.params.id;
    console.log(id);
    Serverservice.eventdetail(id)
      .then((response) => {
        this.setState({
          event: response.data.event,
        });
        console.log(this.state.event);
        console.log(response);
      })
      .catch((err) => {
        console.log(err.response);
      });
  }
  render() {
    return (
      <div className="banner">
        <Navbarr />

        <Container className="mt-5 " fluid>
          <Row>
            <Col>
              <Container>
                <img
                  className="ml-5"
                  src={m2}
                  style={{ width: "60%", borderRadius: "8px", height: "500px" }}
                  alt={"ALternate"}
                />
              </Container>
            </Col>
            <Col lg={8}>
              <Container className="mt-5">
                <h1>{this.state.event.eventname}</h1>
                <h4 className="text-muted">{this.state.event.eventtype}</h4>
                <p className="mt-4">{this.state.event.eventdescription}</p>

                <hr></hr>
                <h4>Event Details</h4>
                <Row className="mt-4">
                  <Col>
                    <h5>City</h5>
                    <h6>{this.state.event.eventcity}</h6>
                  </Col>
                  <Col>
                    <h5>Region</h5>
                    <h6>{this.state.event.eventregion}</h6>
                  </Col>
                  <Col>
                    <h5>Date</h5>
                    <h6>{this.state.event.eventdate}</h6>
                  </Col>
                  <Col>
                    <h5>Expected Seats</h5>
                    <h6>{this.state.event.audience}</h6>
                  </Col>
                  <Col>
                    <h5>Email</h5>
                    <h6>{this.state.event.email}</h6>
                  </Col>
                  <Col>
                    <h5>Ticket Price</h5>
                    <h6>{this.state.event.ticketPrice}</h6>
                  </Col>
                </Row>
                <Form className="mt-5">
                  <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Number of Seats</Form.Label>
                    <Form.Control
                      style={{ width: "100px" }}
                      as="select"
                      name="noofticket"
                      onChange={this.onChange}
                    >
                      <option>1</option>
                      <option>2</option>
                      <option>3</option>
                      <option>4</option>
                      <option>5</option>
                    </Form.Control>
                  </Form.Group>

                  <button
                    type="submit"
                    onClick={this.booktickets(this.state.event.ticketPrice)}
                    className="bookbutton "
                  >
                    Book Tickets
                  </button>
                </Form>
              </Container>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
